    static func randomizeAndHyphenateWords(in input: String) -> String {
        var words = input.components(separatedBy: .whitespaces).filter { !$0.isEmpty }
        words.shuffle()
        
        let hyphenated = words.joined(separator: "-")
        return hyphenated
    }
    
